"use strict";

// Подключение карты
function init() {
  var map = new ymaps.Map('map', {
    center: [55.76111354167797, 37.618721937928335],
    zoom: 14
  });
  var placemark = new ymaps.Placemark([55.76953456898229, 37.63998549999998], {}, {
    iconLayout: 'default#image',
    iconImageHref: './img/placemark.svg',
    iconImageSize: [12, 12],
    iconImageOffset: [0, 0]
  });
  map.controls.remove('geolocationControl');
  map.controls.remove('searchControl');
  map.controls.remove('trafficControl');
  map.controls.remove('typeSelector');
  map.controls.remove('fullscreenControl');
  map.controls.remove('zoomControl');
  map.controls.remove('rulerControl');
  map.geoObjects.add(placemark);
}
ymaps.ready(init);

// Открытие и закрытие строки поиска
var searchBtn = document.getElementById('search-btn');
var searchInput = document.getElementById('search-input');
var closeSearchBtn = document.getElementById('search-btn-close');
searchBtn.addEventListener('click', function (e) {
  e.preventDefault();
  searchInput.classList.add('header__input--active');
  searchBtn.style.visibility = 'hidden';
  closeSearchBtn.style.display = 'block';
});
closeSearchBtn.addEventListener('click', function (e) {
  e.preventDefault();
  searchInput.classList.remove('header__input--active');
  searchBtn.style.visibility = 'visible';
  closeSearchBtn.style.display = 'none';
});

// Закрытие окна с адресом
var closeInfoBtn = document.getElementById('close-info-btn');
closeInfoBtn.addEventListener('click', function (e) {
  e.preventDefault();
  document.querySelector('.contacts__info').classList.add('contacts__info-close');
});

// Откртие и закрытие меню бургер
var closeNavBtn = document.getElementById('close-nav-btn');
var openNavBtn = document.getElementById('open-burger');
openNavBtn.addEventListener('click', function (e) {
  e.preventDefault();
  document.querySelector('.nav').classList.add('active');
});
closeNavBtn.addEventListener('click', function (e) {
  e.preventDefault();
  document.querySelector('.nav').classList.remove('active');
});

// Валидация формы
var validContacts = new JustValidate('#contacts-form');
validContacts.addField('#contacts-form-name', [{
  rule: 'required',
  errorMessage: 'Введите имя'
}]).addField('#contacts-form-email', [{
  rule: 'required',
  errorMessage: 'Введите email'
}, {
  rule: 'email',
  errorMessage: 'Введите email в формате email@mail.com'
}]);
var validSubscription = new JustValidate('#subscription-form');
validSubscription.addField('#subscription-input', [{
  rule: 'required',
  errorMessage: 'Введите email'
}, {
  rule: 'email',
  errorMessage: 'Введите email в формате email@mail.com'
}]);
